if text == 'المشاركين' and redis:get(TheMatrix..":Witting_StartGame:"..msg.chat_id..msg.sender_id.user_id) then
local list = redis:smembers(TheMatrix..':List_Rolet:'..msg.chat_id) 
local Text = '\n  * ٴ— — — — — — — — —*\n'
if #list == 0 then 
bot.sendText(msg.chat_id,msg.id,"*-  لا يوجد لاعبين*","md",true)
return false
end  
for k, v in pairs(list) do 
Text = Text..k.."-   [" ..v.."] .\n"  
end 
return bot.sendText(msg.chat_id,msg.id,Text,"md",true)  
end
if text == 'نعم' and redis:get(TheMatrix..":Witting_StartGame:"..msg.chat_id..msg.sender_id.user_id) then
local list = redis:smembers(TheMatrix..':List_Rolet:'..msg.chat_id) 
if #list == 1 then 
bot.sendText(msg.chat_id,msg.id,"-  لم يكتمل العدد الكلي للاعبين*","md",true)  
elseif #list == 0 then 
bot.sendText(msg.chat_id,msg.id,"*-  لم تقوم باضافه اي لاعب*","md",true)  
return false
end 
local UserName = list[math.random(#list)]
local User_ = UserName:match("^@(.*)$")
local UserId_Info = bot.searchPublicChat(User_)
if (UserId_Info.id) then
redis:incrby(TheMatrix..":"..msg.chat_id..":"..UserId_Info.id..":game", 3)  
redis:del(TheMatrix..':List_Rolet:'..msg.chat_id) 
redis:del(TheMatrix..":Witting_StartGame:"..msg.chat_id..msg.sender_id.user_id)
bot.sendText(msg.chat_id,msg.id,"*-  مبروك * ["..UserName.."] *لقد فزت\n-  تم اضافه 3 نقاط لك\n","md",true)  
return false
end
end
if text and text:match('^(@[%a%d_]+)$') and redis:get(TheMatrix..":Number_Add:"..msg.chat_id..msg.sender_id.user_id) then
if redis:sismember(TheMatrix..':List_Rolet:'..msg.chat_id,text) then
bot.sendText(msg.chat_id,msg.id,"*-  المعرف* ["..text.." ] *موجود من قبل ارسل معرف لم يشارك*","md",true)  
return false
end 
redis:sadd(TheMatrix..':List_Rolet:'..msg.chat_id,text)
local CountAdd = redis:get(TheMatrix..":Number_Add:"..msg.chat_id..msg.sender_id.user_id)
local CountAll = redis:scard(TheMatrix..':List_Rolet:'..msg.chat_id)
local CountUser = CountAdd - CountAll
if tonumber(CountAll) == tonumber(CountAdd) then 
redis:del(TheMatrix..":Number_Add:"..msg.chat_id..msg.sender_id.user_id) 
redis:setex(TheMatrix..":Witting_StartGame:"..msg.chat_id..msg.sender_id.user_id,1400,true)  
bot.sendText(msg.chat_id,msg.id,"*-  تم حفظ المعرف (*["..text.."]*)\n-  ارسل ( نعم ) للبدء*","md",true)  
return false
end  
bot.sendText(msg.chat_id,msg.id,"*-  تم حفظ المعرف* (["..text.."])\n*-  تبقى "..CountUser.." لاعبين ليكتمل العدد\n-  ارسل المعرف التالي*","md",true)  
