function AggetDate(userID)
local ages = {["2768409"] = 1383264000,["7679610"] = 1388448000,["11538514"] = 1391212000,["15835244"] = 1392940000,["23646077"] = 1393459000,["38015510"] = 1393632000,["44634663"] = 1399334000,["46145305"] = 1400198000,["54845238"] = 1411257000,["63263518"] = 1414454000,["101260938"] = 1425600000,["101323197"] = 1426204000,["111220210"] = 1429574000,["103258382"] = 1432771000,["103151531"] = 1433376000,["116812045"] = 1437696000,["122600695"] = 1437782000,["109393468"] = 1439078000,["112594714"] = 1439683000,["124872445"] = 1439856000,["130029930"] = 1441324000,["125828524"] = 1444003000,["133909606"] = 1444176000,["157242073"] = 1446768000,["143445125"] = 1448928000,["148670295"] = 1452211000,["152079341"] = 1453420000,["171295414"] = 1457481000,["181783990"] = 1460246000,["222021233"] = 1465344000,["225034354"] = 1466208000,["278941742"] = 1473465000,["285253072"] = 1476835000,["294851037"] = 1479600000,["297621225"] = 1481846000,["328594461"] = 1482969000,["337808429"] = 1487707000,["341546272"] = 1487782000,["352940995"] = 1487894000,["369669043"] = 1490918000,["400169472"] = 1501459000,["805158066"] = 1563208000,["1974255900"] = 1634000000}
local ids = {}
for k,_ in pairs(ages) do
table.insert(ids, k)
end
table.sort(ids)
local nids = {}
for _,id in ipairs(ids) do
table.insert(nids, tonumber(id))
end
local minId = nids[1]
local maxId = nids[#nids]
if userID < minId then
return {-1, ages[ids[1]]}
elseif userID > maxId then
return {1, ages[ids[#ids]]}
else
local lid = nids[1]
for i = 1, #ids do
if userID <= nids[i] then
local uid = nids[i]
local lage = ages[tostring(lid)]
local uage = ages[tostring(uid)]
local idratio = (userID - lid) / (uid - lid)
local midDate = (idratio * (uage - lage)) + lage
return {0, midDate}
else
lid = nids[i]
end
end
end
end
function getAge(userID)
ar = AggetDate(userID)
dt = os.date("%Y/%m/%d", ar[2])
age = {["older_than"]="أقدم من", ["newer_than"]="أحدث من", ["aprox"]="~"}
if ar[1] < 0 then
v = 'older_than'
elseif ar[1] > 0 then
v = 'newer_than'
else
v = 'aprox'
end
t = "-" .. age[v] .. " " .. dt
return t
end

